import { createSlice } from "@reduxjs/toolkit";

const initialState = { show: false };

const cartSlice = createSlice({
  name: "showShoppingCart",
  initialState,
  reducers: {
    showCart(state) {
      state.show = true;
    },
    hideCart(state) {
      state.show = false;
    },
  },
});

export const cartActions = cartSlice.actions;

export default cartSlice.reducer;
